import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';



function App() {

  const [reactNativeCode, setReactNativeCode] = useState('');
  const [reactJsCode, setReactJsCode] = useState('');

  const handleConvert = () => {
    // Placeholder for the conversion logic
    let convertedCode = reactNativeCode;

    // Example conversion logic: replace "View" with "div" and "Text" with "span"
    convertedCode = convertedCode
      .replace(/View/g, 'div')
      .replace(/Text/g, 'span');

    setReactJsCode(convertedCode);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
